import React from 'react';
import { Package, AlertCircle, Plus } from 'lucide-react';
import { format } from 'date-fns';
import './ProductLibrary.css';

function ProductLibrary({ products, selectedProduct, onSelectProduct, onAddProduct }) {
    const getProductIcon = (category) => {
        switch (category?.toLowerCase()) {
            case 'steel':
            case 'metal':
                return 'M';
            case 'plastic':
                return 'P';
            case 'electronic':
                return 'E';
            case 'textile':
                return 'T';
            case 'chemical':
                return 'C';
            default:
                return 'O';
        }
    };

    const getCategoryColor = (category) => {
        switch (category?.toLowerCase()) {
            case 'steel':
            case 'metal':
                return '#64748b';
            case 'plastic':
                return '#3b82f6';
            case 'electronic':
                return '#10b981';
            case 'textile':
                return '#f59e0b';
            default:
                return '#6b7280';
        }
    };

    return (
        <div className="product-library">
            <div className="library-header">
                <h2 className="library-title">Product Library</h2>
                <button
                    className="add-btn-mobile"
                    onClick={onAddProduct}
                    title="Add New Product"
                >
                    <Plus size={20} />
                </button>
            </div>

            <div className="products-list">
                {products.length === 0 ? (
                    <div className="empty-state">
                        <Package size={48} className="empty-icon" />
                        <p className="empty-text">No products yet</p>
                        <p className="empty-subtext">Add your first product to get started</p>
                        <button
                            className="add-first-btn"
                            onClick={onAddProduct}
                        >
                            <Plus size={16} />
                            Add Product
                        </button>
                    </div>
                ) : (
                    products.map((product) => (
                        <div
                            key={product.id}
                            className={`product-item ${selectedProduct?.id === product.id ? 'selected' : ''}`}
                            onClick={() => onSelectProduct(product)}
                        >
                            <div className="product-icon" style={{ backgroundColor: getCategoryColor(product.category) }}>
                                {getProductIcon(product.category)}
                            </div>

                            <div className="product-info">
                                <div className="product-header">
                                    <h3 className="product-name">{product.name}</h3>
                                    {product.hasAlerts && (
                                        <AlertCircle size={16} className="alert-icon" />
                                    )}
                                </div>

                                <p className="product-description">{product.description}</p>

                                <div className="product-meta">
                                    <span className="product-category">{product.category}</span>
                                    <span className="product-date">
                                        {format(new Date(product.dateAdded), 'MMM d, yyyy')}
                                    </span>
                                </div>

                                <div className="product-stats">
                                    <span className="hs-count">
                                        {product.hsCodes?.length || 0} HS codes
                                    </span>
                                    <span className="confidence">
                                        {product.hsCodes?.[0]?.confidence || 0}% confidence
                                    </span>
                                </div>
                            </div>
                        </div>
                    ))
                )}
            </div>
        </div>
    );
}

export default ProductLibrary;
