import React from 'react';
import { Bell, Plus, Scale } from 'lucide-react';
import './Header.css';

function Header({ onAddProduct, onShowAlerts, alertsCount }) {
  return (
    <header className="header">
      <div className="header-left">
        <div className="logo">
          <Scale className="logo-icon" />
          <span className="logo-text">HS Compliance Copilot</span>
        </div>
      </div>

      <div className="header-right">
        <button 
          className="add-product-btn"
          onClick={onAddProduct}
          title="Add New Product"
        >
          <Plus size={20} />
          <span className="btn-text">Add Product</span>
        </button>

        <button 
          className="alerts-btn"
          onClick={onShowAlerts}
          title="View Tariff Alerts"
        >
          <Bell size={20} />
          {alertsCount > 0 && (
            <span className="alert-badge">{alertsCount}</span>
          )}
        </button>
      </div>
    </header>
  );
}

export default Header;
