import React, { useState } from 'react';
import { X, Package, ChevronRight, ChevronLeft, Check } from 'lucide-react';
import './AddProductModal.css';

function AddProductModal({ onClose, onAddProduct }) {
  const [currentStep, setCurrentStep] = useState(1);
  const [formData, setFormData] = useState({
    name: '',
    description: '',
    category: 'steel',
    material: '',
    dimensions: '',
    intendedUse: '',
    origin: '',
    manufacturer: ''
  });

  const [isSubmitting, setIsSubmitting] = useState(false);

  const totalSteps = 5;

    const categories = [
        { value: 'steel', label: 'Steel/Metal', icon: 'M' },
        { value: 'plastic', label: 'Plastic', icon: 'P' },
        { value: 'electronic', label: 'Electronic', icon: 'E' },
        { value: 'textile', label: 'Textile', icon: 'T' },
        { value: 'chemical', label: 'Chemical', icon: 'C' },
        { value: 'other', label: 'Other', icon: 'O' }
    ];

  const nextStep = () => {
    if (currentStep < totalSteps) {
      setCurrentStep(currentStep + 1);
    }
  };

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!formData.name.trim()) return;

    setIsSubmitting(true);

    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 1000));

    onAddProduct(formData);
    setFormData({ 
      name: '', 
      description: '', 
      category: 'steel',
      material: '',
      dimensions: '',
      intendedUse: '',
      origin: '',
      manufacturer: ''
    });
    setCurrentStep(1);
    setIsSubmitting(false);
  };

  const isStepValid = (step) => {
    switch (step) {
      case 1:
        return formData.name.trim() !== '';
      case 2:
        return formData.description.trim() !== '';
      case 3:
        return formData.category !== '';
      case 4:
        return formData.material.trim() !== '';
      case 5:
        return true; // Final step is always valid
      default:
        return false;
    }
  };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                    <div className="modal-title">
                        <Package size={24} />
                        <h2>Add New Product</h2>
                    </div>
                    <button className="close-btn" onClick={onClose}>
                        <X size={20} />
                    </button>
                </div>

                <form onSubmit={handleSubmit} className="modal-form">
                    <div className="form-group">
                        <label htmlFor="name" className="form-label">
                            Product Name *
                        </label>
                        <input
                            type="text"
                            id="name"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            className="form-input"
                            placeholder="e.g., Steel bolts for construction"
                            required
                            disabled={isSubmitting}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="description" className="form-label">
                            Description
                        </label>
                        <textarea
                            id="description"
                            name="description"
                            value={formData.description}
                            onChange={handleChange}
                            className="form-textarea"
                            placeholder="Provide additional details about the product..."
                            rows={3}
                            disabled={isSubmitting}
                        />
                    </div>

                    <div className="form-group">
                        <label htmlFor="category" className="form-label">
                            Category
                        </label>
                        <div className="category-grid">
                            {categories.map((category) => (
                                <label
                                    key={category.value}
                                    className={`category-option ${formData.category === category.value ? 'selected' : ''}`}
                                >
                                    <input
                                        type="radio"
                                        name="category"
                                        value={category.value}
                                        checked={formData.category === category.value}
                                        onChange={handleChange}
                                        disabled={isSubmitting}
                                        className="category-radio"
                                    />
                                    <div className="category-content">
                                        <span className="category-icon">{category.icon}</span>
                                        <span className="category-label">{category.label}</span>
                                    </div>
                                </label>
                            ))}
                        </div>
                    </div>

                    <div className="form-actions">
                        <button
                            type="button"
                            onClick={onClose}
                            className="cancel-btn"
                            disabled={isSubmitting}
                        >
                            Cancel
                        </button>
                        <button
                            type="submit"
                            className="submit-btn"
                            disabled={!formData.name.trim() || isSubmitting}
                        >
                            {isSubmitting ? (
                                <>
                                    <div className="spinner"></div>
                                    Adding Product...
                                </>
                            ) : (
                                'Add Product'
                            )}
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}

export default AddProductModal;
