import React from 'react';
import { X, AlertTriangle, Info, CheckCircle, Clock } from 'lucide-react';
import { format } from 'date-fns';
import './TariffAlerts.css';

function TariffAlerts({ alerts, onClose, onMarkAsRead }) {
  const getAlertIcon = (type, severity) => {
    if (type === 'tariff_change') {
      return <AlertTriangle size={20} className="alert-icon-high" />;
    } else if (type === 'hs_code_update') {
      return <Info size={20} className="alert-icon-medium" />;
    }
    return <Info size={20} className="alert-icon-low" />;
  };

  const getSeverityColor = (severity) => {
    switch (severity) {
      case 'high':
        return '#ef4444';
      case 'medium':
        return '#f59e0b';
      case 'low':
        return '#3b82f6';
      default:
        return '#6b7280';
    }
  };

  const getSeverityLabel = (severity) => {
    switch (severity) {
      case 'high':
        return 'High Priority';
      case 'medium':
        return 'Medium Priority';
      case 'low':
        return 'Low Priority';
      default:
        return 'Info';
    }
  };

  const unreadAlerts = alerts.filter(alert => !alert.read);
  const readAlerts = alerts.filter(alert => alert.read);

  return (
    <div className="alerts-overlay" onClick={onClose}>
      <div className="alerts-content" onClick={(e) => e.stopPropagation()}>
        <div className="alerts-header">
          <div className="alerts-title">
            <AlertTriangle size={24} />
            <h2>Tariff Alerts</h2>
            {unreadAlerts.length > 0 && (
              <span className="unread-count">{unreadAlerts.length}</span>
            )}
          </div>
          <button className="close-btn" onClick={onClose}>
            <X size={20} />
          </button>
        </div>

        <div className="alerts-body">
          {alerts.length === 0 ? (
            <div className="empty-alerts">
              <CheckCircle size={48} className="empty-icon" />
              <h3>No Alerts</h3>
              <p>You're all caught up! No tariff changes or updates at the moment.</p>
            </div>
          ) : (
            <>
              {unreadAlerts.length > 0 && (
                <div className="alerts-section">
                  <h3 className="section-title">Unread Alerts</h3>
                  <div className="alerts-list">
                    {unreadAlerts.map((alert) => (
                      <div
                        key={alert.id}
                        className={`alert-item unread`}
                        onClick={() => onMarkAsRead(alert.id)}
                      >
                        <div className="alert-icon-wrapper">
                          {getAlertIcon(alert.type, alert.severity)}
                        </div>
                        <div className="alert-content">
                          <div className="alert-header">
                            <h4 className="alert-title">{alert.message}</h4>
                            <span 
                              className="severity-badge"
                              style={{ backgroundColor: getSeverityColor(alert.severity) }}
                            >
                              {getSeverityLabel(alert.severity)}
                            </span>
                          </div>
                          <div className="alert-meta">
                            <span className="alert-date">
                              <Clock size={14} />
                              {format(new Date(alert.date), 'MMM d, yyyy')}
                            </span>
                            <span className="alert-type">
                              {alert.type.replace('_', ' ').toUpperCase()}
                            </span>
                          </div>
                        </div>
                        <div className="alert-actions">
                          <button className="mark-read-btn">
                            Mark as Read
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}

              {readAlerts.length > 0 && (
                <div className="alerts-section">
                  <h3 className="section-title">Read Alerts</h3>
                  <div className="alerts-list">
                    {readAlerts.map((alert) => (
                      <div
                        key={alert.id}
                        className="alert-item read"
                      >
                        <div className="alert-icon-wrapper">
                          {getAlertIcon(alert.type, alert.severity)}
                        </div>
                        <div className="alert-content">
                          <div className="alert-header">
                            <h4 className="alert-title">{alert.message}</h4>
                            <span 
                              className="severity-badge read"
                              style={{ backgroundColor: getSeverityColor(alert.severity) }}
                            >
                              {getSeverityLabel(alert.severity)}
                            </span>
                          </div>
                          <div className="alert-meta">
                            <span className="alert-date">
                              <Clock size={14} />
                              {format(new Date(alert.date), 'MMM d, yyyy')}
                            </span>
                            <span className="alert-type">
                              {alert.type.replace('_', ' ').toUpperCase()}
                            </span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              )}
            </>
          )}
        </div>

        <div className="alerts-footer">
          <button className="dismiss-all-btn" onClick={onClose}>
            Dismiss All
          </button>
        </div>
      </div>
    </div>
  );
}

export default TariffAlerts;
