import React, { useState } from 'react';
import { Card, Button, Icon, Tag, Divider, Collapse, H1, H2, H3, H4, H5, Text } from '@blueprintjs/core';
import { ChevronDown, ChevronUp, Document, Download, Tag as TagIcon } from '@blueprintjs/icons';
import { format } from 'date-fns';
import './ProductDetails.css';

function ProductDetails({ product, onGenerateBTI, onExport }) {
    const [expandedCodes, setExpandedCodes] = useState({});
    const [expandedCitations, setExpandedCitations] = useState({});

  if (!product) {
    return (
      <div className="product-details">
        <Card className="empty-details" elevation={2}>
          <Icon icon={<Document />} iconSize={64} color="#cbd5e1" />
          <H3>Select a Product</H3>
          <Text color="muted">Choose a product from the library to view its HS codes, duty rates, and compliance information.</Text>
        </Card>
      </div>
    );
  }

    const toggleCodeExpansion = (code) => {
        setExpandedCodes(prev => ({
            ...prev,
            [code]: !prev[code]
        }));
    };

    const toggleCitationExpansion = (index) => {
        setExpandedCitations(prev => ({
            ...prev,
            [index]: !prev[index]
        }));
    };

    const getConfidenceColor = (confidence) => {
        if (confidence >= 90) return '#10b981';
        if (confidence >= 75) return '#f59e0b';
        return '#ef4444';
    };

    const getConfidenceLabel = (confidence) => {
        if (confidence >= 90) return 'High';
        if (confidence >= 75) return 'Medium';
        return 'Low';
    };

  return (
    <div className="product-details">
      <Card className="details-header" elevation={2}>
        <div className="product-title-section">
          <H1 className="product-title">{product.name}</H1>
          <Text className="product-subtitle">{product.description}</Text>
          <div className="product-badges">
            <Tag intent="primary" large>{product.category}</Tag>
            <Tag minimal large>
              Added {format(new Date(product.dateAdded), 'MMM d, yyyy')}
            </Tag>
          </div>
        </div>
      </Card>

            <div className="details-content">
                {/* HS Codes Section */}
                <div className="results-section">
                    <h2 className="section-title">
                        <FileText size={20} className="section-icon" />
                        Top HS Codes
                    </h2>
                    <div className="hs-codes-list">
                        {product.hsCodes?.map((hsCode, index) => (
                            <div key={index} className="hs-code-card">
                                <div className="hs-code-header">
                                    <div className="hs-code-info">
                                        <span className="hs-code-number">{hsCode.code}</span>
                                        <span
                                            className="confidence-badge"
                                            style={{ backgroundColor: getConfidenceColor(hsCode.confidence) }}
                                        >
                                            {hsCode.confidence}% {getConfidenceLabel(hsCode.confidence)}
                                        </span>
                                    </div>
                                    <button
                                        className="expand-btn"
                                        onClick={() => toggleCodeExpansion(hsCode.code)}
                                    >
                                        {expandedCodes[hsCode.code] ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
                                    </button>
                                </div>
                                <p className="hs-code-description">{hsCode.description}</p>
                                {expandedCodes[hsCode.code] && (
                                    <div className="hs-code-details">
                                        <div className="detail-row">
                                            <span className="detail-label">Classification:</span>
                                            <span className="detail-value">{hsCode.code}</span>
                                        </div>
                                        <div className="detail-row">
                                            <span className="detail-label">Confidence Score:</span>
                                            <span className="detail-value">{hsCode.confidence}%</span>
                                        </div>
                                        <div className="detail-row">
                                            <span className="detail-label">Ranking:</span>
                                            <span className="detail-value">#{index + 1}</span>
                                        </div>
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                </div>

                {/* Duty Rates Section */}
                <div className="results-section">
                    <h2 className="section-title">
                        <Download size={20} className="section-icon" />
                        Duty Rates by Region
                    </h2>
                    <div className="duty-rates-grid">
                        {Object.entries(product.dutyRates || {}).map(([region, rate]) => (
                            <div key={region} className="duty-rate-card">
                                <div className="region-name">{region}</div>
                                <div className="duty-rate">{rate.rate}</div>
                                <div className="rate-type">{rate.type}</div>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Citations Section */}
                <div className="results-section">
                    <h2 className="section-title">
                        <FileText size={20} className="section-icon" />
                        Regulatory Citations
                    </h2>
                    <div className="citations-list">
                        {product.citations?.map((citation, index) => (
                            <div key={index} className="citation-card">
                                <div className="citation-header">
                                    <div className="citation-source">
                                        <span className="source-name">{citation.source}</span>
                                        <span className="source-ref">{citation.reference}</span>
                                    </div>
                                    <button
                                        className="expand-btn"
                                        onClick={() => toggleCitationExpansion(index)}
                                    >
                                        {expandedCitations[index] ? <ChevronUp size={16} /> : <ChevronDown size={16} />}
                                    </button>
                                </div>
                                <p className="citation-excerpt">{citation.excerpt}</p>
                                {expandedCitations[index] && (
                                    <div className="citation-details">
                                        <div className="detail-row">
                                            <span className="detail-label">Date:</span>
                                            <span className="detail-value">{citation.date}</span>
                                        </div>
                                        <div className="detail-row">
                                            <span className="detail-label">Source:</span>
                                            <span className="detail-value">{citation.source}</span>
                                        </div>
                                    </div>
                                )}
                            </div>
                        ))}
                    </div>
                </div>

                {/* BTI Generator Section */}
                <div className="results-section">
                    <h2 className="section-title">
                        <FileText size={20} className="section-icon" />
                        BTI Draft Generator
                    </h2>
                    <div className="bti-section">
                        <p className="bti-description">
                            Generate a Binding Tariff Information (BTI) draft for this product based on the recommended HS codes.
                        </p>
                        <button className="bti-generate-btn" onClick={onGenerateBTI}>
                            <FileText size={20} />
                            Generate BTI Draft
                        </button>
                    </div>
                </div>

                {/* Export Section */}
                <div className="results-section">
                    <h2 className="section-title">
                        <Download size={20} className="section-icon" />
                        Export Data
                    </h2>
                    <div className="export-buttons">
                        <button
                            className="export-btn"
                            onClick={() => onExport('csv')}
                        >
                            <Download size={16} />
                            CSV
                        </button>
                        <button
                            className="export-btn"
                            onClick={() => onExport('json')}
                        >
                            <Download size={16} />
                            JSON
                        </button>
                        <button
                            className="export-btn"
                            onClick={() => onExport('pdf')}
                        >
                            <Download size={16} />
                            PDF
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ProductDetails;
