import React, { useState } from 'react';
import { Dialog, Button, FormGroup, InputGroup, TextArea, RadioGroup, Radio, ProgressBar, Icon } from '@blueprintjs/core';
import { ChevronRight, ChevronLeft, Tick, Package } from '@blueprintjs/icons';
import './AddProductModal.css';

function AddProductModal({ onClose, onAddProduct }) {
    const [currentStep, setCurrentStep] = useState(1);
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        category: 'steel',
        material: '',
        dimensions: '',
        intendedUse: '',
        origin: '',
        manufacturer: ''
    });

    const [isSubmitting, setIsSubmitting] = useState(false);

    const totalSteps = 5;

    const categories = [
        { value: 'steel', label: 'Steel/Metal', icon: 'M' },
        { value: 'plastic', label: 'Plastic', icon: 'P' },
        { value: 'electronic', label: 'Electronic', icon: 'E' },
        { value: 'textile', label: 'Textile', icon: 'T' },
        { value: 'chemical', label: 'Chemical', icon: 'C' },
        { value: 'other', label: 'Other', icon: 'O' }
    ];

    const nextStep = () => {
        console.log('Next step clicked. Current step:', currentStep, 'Total steps:', totalSteps);
        console.log('Is current step valid?', isStepValid(currentStep));

        if (currentStep < totalSteps && isStepValid(currentStep)) {
            console.log('Moving to next step:', currentStep + 1);
            setCurrentStep(currentStep + 1);
        } else {
            console.log('Cannot move to next step. Validation failed or at last step.');
        }
    };

    const prevStep = () => {
        console.log('Previous step clicked. Current step:', currentStep);
        if (currentStep > 1) {
            console.log('Moving to previous step:', currentStep - 1);
            setCurrentStep(currentStep - 1);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!formData.name.trim()) return;

        setIsSubmitting(true);

        // Simulate API call delay
        await new Promise(resolve => setTimeout(resolve, 1000));

        onAddProduct(formData);
        setFormData({
            name: '',
            description: '',
            category: 'steel',
            material: '',
            dimensions: '',
            intendedUse: '',
            origin: '',
            manufacturer: ''
        });
        setCurrentStep(1);
        setIsSubmitting(false);
    };

    const isStepValid = (step) => {
        let isValid = false;
        switch (step) {
            case 1:
                isValid = formData.name.trim() !== '';
                break;
            case 2:
                isValid = formData.description.trim() !== '';
                break;
            case 3:
                isValid = formData.category !== '';
                break;
            case 4:
                isValid = formData.material.trim() !== '';
                break;
            case 5:
                isValid = true; // Final step is always valid
                break;
            default:
                isValid = false;
        }
        console.log(`Step ${step} validation:`, isValid, formData);
        return isValid;
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const renderStep = () => {
        switch (currentStep) {
            case 1:
                return (
                    <div className="step-content">
                        <div className="step-header">
                            <h3>What is the product name?</h3>
                            <p>Enter a clear, descriptive name for your product</p>
                        </div>
                        <div className="form-group">
                            <input
                                type="text"
                                name="name"
                                value={formData.name}
                                onChange={handleChange}
                                className="form-input"
                                placeholder="e.g., Steel bolts for construction"
                                disabled={isSubmitting}
                                autoFocus
                            />
                        </div>
                    </div>
                );

            case 2:
                return (
                    <div className="step-content">
                        <div className="step-header">
                            <h3>Describe the product</h3>
                            <p>Provide detailed information about what this product is and how it's used</p>
                        </div>
                        <div className="form-group">
                            <textarea
                                name="description"
                                value={formData.description}
                                onChange={handleChange}
                                className="form-textarea"
                                placeholder="Describe the product's appearance, function, and typical applications..."
                                rows={4}
                                disabled={isSubmitting}
                                autoFocus
                            />
                        </div>
                    </div>
                );

            case 3:
                return (
                    <div className="step-content">
                        <div className="step-header">
                            <h3>Select product category</h3>
                            <p>Choose the most appropriate category for your product</p>
                        </div>
                        <div className="category-grid">
                            {categories.map((category) => (
                                <label
                                    key={category.value}
                                    className={`category-option ${formData.category === category.value ? 'selected' : ''}`}
                                >
                                    <input
                                        type="radio"
                                        name="category"
                                        value={category.value}
                                        checked={formData.category === category.value}
                                        onChange={handleChange}
                                        disabled={isSubmitting}
                                        className="category-radio"
                                    />
                                    <div className="category-content">
                                        <span className="category-icon">{category.icon}</span>
                                        <span className="category-label">{category.label}</span>
                                    </div>
                                </label>
                            ))}
                        </div>
                    </div>
                );

            case 4:
                return (
                    <div className="step-content">
                        <div className="step-header">
                            <h3>What materials is it made of?</h3>
                            <p>Specify the primary materials used in this product</p>
                        </div>
                        <div className="form-group">
                            <input
                                type="text"
                                name="material"
                                value={formData.material}
                                onChange={handleChange}
                                className="form-input"
                                placeholder="e.g., Carbon steel, Stainless steel, Aluminum..."
                                disabled={isSubmitting}
                                autoFocus
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="dimensions" className="form-label">
                                Dimensions (optional)
                            </label>
                            <input
                                type="text"
                                id="dimensions"
                                name="dimensions"
                                value={formData.dimensions}
                                onChange={handleChange}
                                className="form-input"
                                placeholder="e.g., M8 x 20mm, 100mm x 50mm x 25mm..."
                                disabled={isSubmitting}
                            />
                        </div>
                    </div>
                );

            case 5:
                return (
                    <div className="step-content">
                        <div className="step-header">
                            <h3>Additional information</h3>
                            <p>Provide any additional details that might help with classification</p>
                        </div>
                        <div className="form-group">
                            <label htmlFor="intendedUse" className="form-label">
                                Intended Use
                            </label>
                            <input
                                type="text"
                                id="intendedUse"
                                name="intendedUse"
                                value={formData.intendedUse}
                                onChange={handleChange}
                                className="form-input"
                                placeholder="e.g., Construction, Automotive, Electronics..."
                                disabled={isSubmitting}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="origin" className="form-label">
                                Country of Origin
                            </label>
                            <input
                                type="text"
                                id="origin"
                                name="origin"
                                value={formData.origin}
                                onChange={handleChange}
                                className="form-input"
                                placeholder="e.g., Germany, China, USA..."
                                disabled={isSubmitting}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="manufacturer" className="form-label">
                                Manufacturer (optional)
                            </label>
                            <input
                                type="text"
                                id="manufacturer"
                                name="manufacturer"
                                value={formData.manufacturer}
                                onChange={handleChange}
                                className="form-input"
                                placeholder="e.g., Bosch, Siemens, Generic..."
                                disabled={isSubmitting}
                            />
                        </div>
                    </div>
                );

            default:
                return null;
        }
    };

    return (
        <div className="modal-overlay" onClick={onClose}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
                <div className="modal-header">
                    <div className="modal-title">
                        <Package size={24} />
                        <h2>Add New Product</h2>
                    </div>
                    <button className="close-btn" onClick={onClose}>
                        <X size={20} />
                    </button>
                </div>

                <div className="step-progress">
                    <div className="progress-bar">
                        <div
                            className="progress-fill"
                            style={{ width: `${(currentStep / totalSteps) * 100}%` }}
                        ></div>
                    </div>
                    <div className="step-indicators">
                        {Array.from({ length: totalSteps }, (_, i) => i + 1).map((step) => (
                            <div
                                key={step}
                                className={`step-indicator ${currentStep >= step ? 'active' : ''} ${currentStep === step ? 'current' : ''}`}
                            >
                                {currentStep > step ? <Check size={16} /> : step}
                            </div>
                        ))}
                    </div>
                    <div className="step-text">
                        Step {currentStep} of {totalSteps}
                    </div>
                    {/* Debug info - remove in production */}
                    <div style={{ fontSize: '0.75rem', color: '#666', marginTop: '0.5rem', textAlign: 'center' }}>
                        Debug: Step {currentStep}, Valid: {isStepValid(currentStep) ? 'Yes' : 'No'}
                    </div>
                </div>

                <div className="modal-form">
                    {renderStep()}

                    <div className="form-actions">
                        {currentStep > 1 && (
                            <button
                                type="button"
                                onClick={prevStep}
                                className="back-btn"
                                disabled={isSubmitting}
                            >
                                <ChevronLeft size={16} />
                                Back
                            </button>
                        )}

                        {currentStep < totalSteps ? (
                            <button
                                type="button"
                                onClick={nextStep}
                                className="next-btn"
                                disabled={!isStepValid(currentStep) || isSubmitting}
                            >
                                Next
                                <ChevronRight size={16} />
                            </button>
                        ) : (
                            <button
                                type="button"
                                onClick={handleSubmit}
                                className="submit-btn"
                                disabled={!isStepValid(currentStep) || isSubmitting}
                            >
                                {isSubmitting ? (
                                    <>
                                        <div className="spinner"></div>
                                        Adding Product...
                                    </>
                                ) : (
                                    <>
                                        <Check size={16} />
                                        Add Product
                                    </>
                                )}
                            </button>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
}

export default AddProductModal;
